Source code in <code>cgatcore/pipeline/cluster.py</code> <pre><code>@classmethod
def parse_accounting_data(cls, data, retval):

    def convert_value(*pair):
        k, v = pair[0]
        if k in ("UserCPU", "SystemCPU"):
            n = 0
            for x, f in zip(re.split("[-:]", v)[::-1], (1, 60, 3600, 86400)):
                n += float(x) * f
            v = n
        elif k in ("Start", "End", "Submit"):
            try:
                v = time.mktime(datetime.datetime.strptime(
                    v, "%Y-%m-%dT%H:%M:%S").timetuple())
            except ValueError as ex:
                E.warning("could not parse time value '{}' for field '{}': {}".format(
                    v, k, ex))
                v = None
        elif k in ("ExitCode", ):
            v = int(v.split(":")[0])
        elif v.endswith("K"):
            v = float(v[:-1]) * 1000
        elif v.endswith("M"):
            v = float(v[:-1]) * 1000000
        elif v.endswith("G"):
            v = float(v[:-1]) * 1000000000
        try:
            v = int(v)
        except ValueError:
            pass
        return k, v

    d = dict(map(convert_value, zip(
        cls.map_drmaa2benchmark_data.values(), data.split("|"))))
    retval = retval._replace(resourceUsage=d)
    return [retval]
