        cpu_value = data.get("percent_cpu", "0")
        # Strip potential '%' symbols, handle non-numeric cases gracefully
        try:
            percent_cpu = int(re.sub("%", "", cpu_value)) if cpu_value.replace("%", "").strip().isdigit() else 0
